		private void ClearEditValue(IEnumerable controls)
		{
			foreach (System.Windows.Forms.Control control1 in controls)
			{
				if (control1 is DevExpress.XtraEditors.BaseEdit)
					((DevExpress.XtraEditors.BaseEdit)control1).EditValue = DBNull.Value;
				if (control1.Controls.Count > 0)
				{
					ClearEditValue(control1.Controls);
				}
