		///<summary>
		/// Refactored from LoadImage
		///</summary>
		private void LoadAfterPhoto(string path)
		{
			if (path.Length == 0)
			{
				picedtAfter.Image = null;
				lblAfterPhotoMsg.Text = "";
				return;
			}
			try
			{
				Image img1 = Image.FromFile(path);
				picedtAfter.Image = img1;
				lblAfterPhotoMsg.Text = "";
			}
			catch (FileNotFoundException e)
			{
				picedtAfter.Image = null;
				lblAfterPhotoMsg.Text = "After photo image file (" +e.Message +") can not found.\nThe image will not loaded.";
			}
			catch (BadImageFormatException e)
			{
				picedtAfter.Image = null;
				lblAfterPhotoMsg.Text = "After photo image format (" +e.Message +") is incorect.\nThe image will not loaded.";
			}
			catch(Exception e)
			{
				picedtAfter.Image = null;
				lblAfterPhotoMsg.Text = e.Message +"\nError while loading after photo image file. Image file will not loaded.\n";
			}
		}

