        //Derek DNC Update 2014-02-26
		private bool UpdateMemberRecord(DataRow currRow)
		{			
			string auditRemarks = currRow["strRemarks"].ToString();

			if (currRow["strMemberName"].ToString().Length == 0)
			{
				UI.ShowErrorMessage(this, "Please don't enter empty member name.", "Error");
				txtMemberName.Focus();
				return false;
			}

			if (currRow["strNRICFIN"].ToString().Length == 0)
			{
				UI.ShowErrorMessage(this, "Please don't enter empty NRIC/FIN.", "Error");
				txtNRICFIN.Focus();
				return false;
			}

            if (luedtMediaSource.Text.Length == 0)
            {
                UI.ShowErrorMessage(this, "Please Key in Media Source.", "Error");
                luedtMediaSource.Focus();
                return false;
            }

            if (luedtMediaSourceCategory.Text.Length == 0)
            {
                UI.ShowErrorMessage(this, "Please Select Media Source Category.", "Error");
                luedtMediaSourceCategory.Focus();
                return false;
            }

            if (!IsMobileNoValid(currRow["strMobileNo"].ToString()))
            {
                UI.ShowErrorMessage(this, "Please Key in correct mobile no format.\n(eg. 81234567, 63366822)", "Error");
                this.txtMobileNo.Focus();
                return false;
            }

			if (Convert.ToBoolean(currRow["fSingaporean"]))
			{
				if (!MemberRecord.ValidateNRICFormat(txtNRICFIN.Text))
				{
					if (UI.ShowYesNoMessage(this, "NIRC is not in valid format (eg. A1234567B). Do you want enter again?", "Reminder"))
					{
						txtNRICFIN.Focus();
						return false;
					}
				}

				if (myMemberRecord.CheckDuplicateNRIC(currRow["strNRICFIN"].ToString(), currRow["strMembershipID"].ToString()) >= 1)
				{
					UI.ShowErrorMessage(this, "This NRIC already registed by other person.", "Duplicate NRIC");
					txtNRICFIN.Focus();
					return false;
				}
			}

            if (IsEmailValid(currRow["strEmail"].ToString()) || currRow["strEmail"].ToString() == "") { }
            else
            {
                UI.ShowErrorMessage(this, "Please Key in correct Email Address format.\n(eg. albertlim@gmail.com)", "Error");
                this.txtEmail1.Focus();
                return false;
            }

            if (IsEmailValid(currRow["strAltEmail"].ToString()) || currRow["strAltEmail"].ToString() == "") { }
            else
            {
                UI.ShowErrorMessage(this, "Please Key in correct Alternate Email Address format.\n(eg. albertlim@gmail.com)", "Error");
                this.txtEmail2.Focus();
                return false;
            }
			
			Image.GetThumbnailImageAbort myCallback = new Image.GetThumbnailImageAbort(ThumbnailCallback);
			System.IO.MemoryStream ms = new System.IO.MemoryStream();

			byte[] imgPhoto;

			if (picedtMemberPhoto.Image != null)
			{
				int newWidth = 180;
				int newHeight = 180;

				if (picedtMemberPhoto.Image.Width > newWidth || picedtMemberPhoto.Image.Height > newHeight)
				{
					double widthRatio = (double) picedtMemberPhoto.Image.Width / (double) newWidth;
					double heightRatio = (double) picedtMemberPhoto.Image.Height / (double) newHeight;
					double ratio = Math.Max(widthRatio, heightRatio);
					newWidth = (int) (picedtMemberPhoto.Image.Width / ratio);
					newHeight = (int) (picedtMemberPhoto.Image.Height / ratio);
				}
				picedtMemberPhoto.Image.GetThumbnailImage(newWidth, newHeight, myCallback, IntPtr.Zero).Save(ms, picedtMemberPhoto.Image.RawFormat);
				imgPhoto = ms.GetBuffer();
				//auditRemarks = auditRemarks + "updated by " + oUser.NEmployeeID();
			}
			else
			{
				imgPhoto = new Byte[0];
			}

            //Derek DNC Update 2014-03-03
			bool success = false;

            //checkEditPhoneCall "fPhoneCall" --
            //checkEditDNC "fDNC" XX

			success = myMemberRecord.UpdateMember(currRow["strMembershipID"].ToString(), 
                                                    currRow["dtDOB"], 
				                                    Convert.ToBoolean(currRow["fAirCrew"]), 
                                                    Convert.ToBoolean(currRow["fBounceCheck"]),
				                                    Convert.ToBoolean(currRow["fEmail"]), 
                                                    Convert.ToBoolean(currRow["fGiroFailed"]), 
				                                    Convert.ToBoolean(currRow["fPostalMail"]), 
                                                    Convert.ToBoolean(currRow["fSingaporean"]), 
				                                    Convert.ToBoolean(currRow["fSMS"]), 
                                                    Convert.ToInt32(currRow["nLoyaltyStatusID"]),
				                                    currRow["strAddress1"].ToString(), 
                                                    currRow["strAddress2"].ToString(), 
                                                    currRow["strAltEmail"].ToString(), 
				                                    currRow["strCardName"].ToString(), 
                                                    currRow["strCompany"].ToString(), 
                                                    currRow["strCreditCardNo"].ToString(),
				                                    currRow["strEmail"].ToString(), 
                                                    currRow["strEmergencyContactNumber"].ToString(),
				                                    currRow["strEmergencyContactPerson"].ToString(), 
                                                    currRow["strHomeNo"].ToString(), 
				                                    currRow["strMemberName"].ToString(), 
                                                    currRow["strMobileNo"].ToString(), 
                                                    currRow["strNRICFIN"].ToString(), 
				                                    currRow["strOccupation"].ToString(), 
                                                    currRow["strOfficeNo"].ToString(), 
                                                    currRow["strPagerNo"].ToString(), 
				                                    currRow["strPostalCode"].ToString(), 
                                                    auditRemarks, 
				                                    Convert.ToInt32(currRow["nMediaSourceID"]), 
                                                    currRow["strMediaSource"].ToString(),
				                                    currRow["strBeforePhoto"].ToString(), 
                                                    currRow["strAfterPhoto"].ToString(), 
                                                    imgPhoto,
                                                    oUser, 
                                                    Convert.ToBoolean(currRow["fFemale"]),
                                                    Convert.ToBoolean(currRow["fPhoneCall"]),
                                                    currRow["strSecurityQuestion"].ToString(),
                                                    currRow["strSecurityAnswer"].ToString(),
                                                    currRow["strPassword"].ToString());

            if (success)
            {
                UI.ShowMessage(this, "Update member successfully.");
            }
            else
            {
                UI.ShowErrorMessage(this, "Failed to update member.", "Error");
            }			
			
			RefreshMemberGrid();
			return success;
		}

