				if (!ACMS.Convert.ToBoolean(row["fMember"]))
				{
					if (!UI.ShowYesNoMessage(this, "Are you sure you want to convert this to member?"))
						return;

                    // 12-11-2012 Make sure MediaSource should save before convert to member
                    ACMSDAL.TblMember sqlCheckMediaSource = new ACMSDAL.TblMember();
                    int myDTransactionID = sqlCheckMediaSource.CheckMediaSource(CurrentMembershipID);

                    if (myDTransactionID == 0)
                        {
                            UI.ShowErrorMessage(this, "Please Key in Media Source.");
                            luedtMediaSource.Focus();
                            return;
                        }

					try
					{
						myNewMembershipID = myMemberRecord.ConvertMember(row, oUser);
					}
					catch (Exception ex)
					{
						if (ex.Message.IndexOf("PK_tblMember_1") >= 0 || ex.InnerException.Message.IndexOf("PK_tblMember_1") >= 0)
						{
							UI.ShowErrorMessage("Duplicate ID found. Please reset membership ID.");
							return;
						}
					}
					MessageBox.Show(this, "New membership ID is " +myNewMembershipID +".");
					cmbMemberRecordQuery.SelectedIndex = 0;

				}
				else
				{
					UI.ShowErrorMessage(this, "This already is member, so you can't perform convert function.", "Error");
				}
			}
            RefreshMemberGrid();
		}

		

