		public void RefreshMemberGrid()
		{
			if (!isFinishedInit)
				return;
			if (cmbMemberRecordQuery.SelectedIndex == 3)
				return;

			//Remember last select member
			string membershipID = string.Empty;
			if (gvMemberRecord.FocusedRowHandle >= 0)
				membershipID = gvMemberRecord.GetDataRow(gvMemberRecord.FocusedRowHandle)["strMembershipID"].ToString();
			//Set to new member if not empty
			if (myNewMembershipID.Length != 0)
			{
				membershipID = myNewMembershipID;
				myNewMembershipID = string.Empty;
			}

			myMemberRecord.RefreshPreset(cmbMemberRecordQuery.SelectedIndex, "", terminalUser.Branch.Id);//oUser.StrBranchCode());
			UI.ClearDataBinding(GroupControl1.Controls);
			UI.ClearDataBinding(groupMemberRecordPhoto.Controls);
			Remove_EditValueChanged_Event(GroupControl1.Controls);
			InitBranchMemberShip();

			SetCurrentMembershipID();

			SetMediaSource();

			//Set back last select member
			if (membershipID.Length != 0)
			{
				for (int i = 0; i < gvMemberRecord.RowCount; i++)
				{
					GridColumn column1 = null;
					if (gvMemberRecord.GetRowCellValue(i, GridColumn2).ToString() == membershipID)
					{
						gvMemberRecord.ClearSelection();
						gvMemberRecord.FocusedRowHandle = i;
						gvMemberRecord.SelectRow(i);
						gvMemberRecord.FocusedColumn = column1;
						datagridMemberRecord.Focus();
						break;
					}
				}
			}
		}

