		private void RefreshMemberRecordCardRequest()
		{
			GridControl1.DataSource = myMemberRecord.GetCardRequestAudit(CurrentMembershipID);

			DataTable myDataSource = myMemberRecord.GetCardRequest(CurrentMembershipID);
			txtCurrBranch.DataBindings.Clear();
			txtDestBranch.DataBindings.Clear();
			txtCurrStatus.DataBindings.Clear();
			txtDate.DataBindings.Clear();
			txtTransactedBy.DataBindings.Clear();
			txtTransactedByName.DataBindings.Clear();
			txtCurrBranch.Text = "";
			txtDestBranch.Text = "";
			txtCurrStatus.Text = "";
			txtDate.Text = "";
			txtTransactedByName.Text = "";
			if (myDataSource.Rows.Count > 0)
			{
				rowCardRequest = myDataSource.Rows[0];
				txtCurrBranch.DataBindings.Add(new Binding("EditValue", myDataSource, "strCurrentBranchCode"));
				txtDestBranch.DataBindings.Add(new Binding("EditValue", myDataSource, "strDestBranchCode"));
				txtCurrStatus.DataBindings.Add(new Binding("EditValue", myDataSource, "strStatus"));
				txtDate.DataBindings.Add(new Binding("EditValue", myDataSource, "dtLastEditDate"));
				txtTransactedBy.DataBindings.Add(new Binding("EditValue", myDataSource, "nEmployeeID"));	
				txtTransactedByName.DataBindings.Add(new Binding("EditValue", myDataSource, "strEmployeeName"));

				//Enable or disable button
				if (ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.Cancelled || 
					ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.CardIssued ||
					//terminalUser.Branch.Id.Trim() != rowCardRequest["strDestBranchCode"].ToString().Trim() ||
					!(ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.InTransit ||
					ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.TransferRequest))
				{
					sbtnMemberCardReceived.Enabled = false;
				}
				else
				{
					sbtnMemberCardReceived.Enabled = true;
				}
				if (ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.Cancelled || 
					ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.CardIssued ||
					terminalUser.Branch.Id.Trim() != rowCardRequest["strDestBranchCode"].ToString().Trim() ||
					ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) != (int)CardStatusType.CardReceived)
				{
					SimpleButton1.Enabled = false; //Issue
				}
				else
				{
					SimpleButton1.Enabled = true;
				}
				if (ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.Cancelled || 
					ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.CardIssued ||
					terminalUser.Branch.Id.Trim() != rowCardRequest["strCurrentBranchCode"].ToString().Trim())
				{
					sbtnMemberCardCancelled.Enabled = false;
				}
				else
				{
					sbtnMemberCardCancelled.Enabled = true;
				}
				if (ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.Cancelled || 
					ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.CardIssued ||
					ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) != (int)CardStatusType.CardReceived)
				{
					SimpleButton4.Enabled = false; //Request Transfer
				}
				else
				{
					SimpleButton4.Enabled = true;
				}
				if (!(ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.Cancelled ||
					ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.CardIssued || 
					ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.CardReceived) ||
					terminalUser.Branch.Id.Trim() != rowCardRequest["strCurrentBranchCode"].ToString().Trim())
				{
					SimpleButton5.Enabled = true; //Reprint
				}
				else
				{
					SimpleButton5.Enabled = true;
				}
			}
			else
			{
				rowCardRequest = null;
				if (terminalUser.Branch.Id.Trim() != 
					gvMemberRecord.GetDataRow(gvMemberRecord.FocusedRowHandle)["strBranchCode"].ToString().Trim())
					SimpleButton5.Enabled = true; //Reprint
				else
					SimpleButton5.Enabled = true;
				sbtnMemberCardReceived.Enabled = false;
				SimpleButton1.Enabled = false; //Issue
				sbtnMemberCardCancelled.Enabled = false;
				SimpleButton4.Enabled = false; //Request Transfer
			}
		}

		// Card Request Transfer
		private void SimpleButton4_Click(object sender, System.EventArgs e)
		{
			if (rowCardRequest != null)
			{
				if (ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.Cancelled ||
					ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.CardIssued)
				{
					UI.ShowErrorMessage(this, "Card have been Issue or Cancelled can be transact again.");
					return;
				}

				frmRequestTransfer frm = new frmRequestTransfer();
				if (frm.ShowDialog() == DialogResult.OK)
				{
					if (string.Compare(frm.DestinationBranch.Trim(), rowCardRequest["strCurrentBranchCode"].ToString().Trim(), true)
						== 0)
					{
						UI.ShowErrorMessage(this, "Can't request transfer to same branch.");
						frm.Dispose();
						return;
					}

					if (myMemberRecord.UpdateCardStatus(CurrentMembershipID, oUser, CardStatusType.TransferRequest, 
						ACMS.Convert.ToInt32(rowCardRequest["nRequestID"]), frm.DestinationBranch))
					{
						UI.ShowMessage(this, "Successed Request Transfer to " +frm.DestinationBranch +".");
					}
				}
				frm.Dispose();
			}
			RefreshMemberGrid();
			RefreshMemberRecordCardRequest();
		}

		// Issue Card
		private void SimpleButton1_Click(object sender, System.EventArgs e)
		{
			if (rowCardRequest != null)
			{
				DataRow rMember = gvMemberRecord.GetDataRow(gvMemberRecord.FocusedRowHandle);
				if (terminalUser.Branch.Id.Trim() != rMember["strCardBranchCode"].ToString().Trim())
				{
					UI.ShowErrorMessage(this, "This is not the requested collection Branch!");
					return;
				}

				if (ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.Cancelled ||
					ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) == (int)CardStatusType.CardIssued)
				{
					UI.ShowErrorMessage(this, "Card Issued or Cancelled cannot be transact again.");
					return;
				}

				if (ACMS.Convert.ToInt32(rowCardRequest["nStatusID"]) != (int)CardStatusType.CardReceived)
				{
					UI.ShowErrorMessage(this, "Only card that are Received can be issued.");
					return;
				}

				if (!UI.ShowYesNoMessage(this, "Issue Card to Member?"))
					return;

				myMemberRecord.UpdateCardStatus(CurrentMembershipID, oUser, CardStatusType.CardIssued, 
					ACMS.Convert.ToInt32(rowCardRequest["nRequestID"]), rowCardRequest["strDestBranchCode"].ToString());
			}
			RefreshMemberGrid();
			RefreshMemberRecordCardRequest();
		}

		// Reprint Card
		private void SimpleButton5_Click(object sender, System.EventArgs e)
		{
			DataRow rMember = gvMemberRecord.GetDataRow(gvMemberRecord.FocusedRowHandle);
//			if (terminalUser.Branch.Id.Trim() != rMember["strBranchCode"].ToString().Trim())
//			{
//				UI.ShowErrorMessage(this, "This branch is not same with member belonging branch.");
//				return;
//			}







			DataSet tabBranchTable = new DataSet();

            string strSQL = "select nStatusID from tblCardRequest c where c.strMembershipID='" + CurrentMembershipID  + "' and dtLastEditDate=(select MAX(dtLastEditDate) from tblCardRequest c2 where c.strMembershipID = c2.strMembershipID)";
            SqlHelper.FillDataset(connection, CommandType.StoredProcedure, "UP_GETDATA", tabBranchTable, new string[] { "Table" }, new SqlParameter("@strSQL", strSQL));
            DataTable dtMaster = tabBranchTable.Tables["Table"];

            if (dtMaster.Rows.Count > 0)
            {
                if (Convert.ToInt16(dtMaster.Rows[0]["nStatusID"]) == 0 || Convert.ToInt16(dtMaster.Rows[0]["nStatusID"]) == 1 || Convert.ToInt16(dtMaster.Rows[0]["nStatusID"]) == 3)
                {
                    UI.ShowErrorMessage(this, "This request cannot be proceed because member card is already in printing process");
                    return;
                }
                else
                {
                    if (!UI.ShowYesNoMessage(this, "Are you sure you want to Request Print Card?"))
                        return;

                    myMemberRecord.Reprint(CurrentMembershipID, oUser, CardStatusType.RequestPrint, terminalUser.Branch.Id);
                    RefreshMemberGrid();
                    RefreshMemberRecordCardRequest();
                }
            }
            else
            {
                if (!UI.ShowYesNoMessage(this, "Are you sure you want to Request Print Card?"))
                    return;

                myMemberRecord.Reprint(CurrentMembershipID, oUser, CardStatusType.RequestPrint, terminalUser.Branch.Id);
                RefreshMemberGrid();
                RefreshMemberRecordCardRequest();
            }			
		}

