            ACMS.XtraUtils.GridViewUtils.UpdateData(GridView17);

            DataRow r = GridView17.GetDataRow(GridView17.FocusedRowHandle);

            if (r != null)
            {
                ACMSLogic.Printing myReceiptPrinting = new Printing();
                myReceiptPrinting.WriteLine("Reservation List");
                string strLine = "";


                strLine = string.Format("Branch:{0} {1} {2}",
                    ACMS.Convert.ToDateTime(r["dtDate"]).ToString("dd/MM/yyyy"),
                    ACMS.Convert.ToDateTime(r["dtStartTime"]).ToString("T"), r["strClassCode"].ToString());
                myReceiptPrinting.WriteLine(strLine);
                myReceiptPrinting.WriteLine(" ");
                myReceiptPrinting.WriteLine("Please Use List To Issue Entry Pass.");
                myReceiptPrinting.WriteLine(" ");

                DataTable reservedMemberTable = null;

                if (GridControl19.DataSource != null)
                {
                    reservedMemberTable = (DataTable)GridControl19.DataSource;
                    //reservedMemberTable.Select(null,reservedMemberTable.Columns["strMembershipID"].ColumnName); 
                    reservedMemberTable.DefaultView.Sort = "[" + reservedMemberTable.Columns["strMembershipID"].ColumnName + "] desc";
                    //output to console for debugging 					

                    if (reservedMemberTable != null &&
                        reservedMemberTable.Rows.Count > 0)
                    {
                        for (int i = 0; i < reservedMemberTable.Rows.Count; i++)
                        {
                            int j = i + 1;
                            int k = j + 1;

                            if (j < reservedMemberTable.Rows.Count)
                            {
                                strLine = string.Format(j + " --{0} \n" + k + " --{1}",
                                    reservedMemberTable.Rows[i]["strMembershipID"].ToString(),
                                    reservedMemberTable.Rows[j]["strMembershipID"].ToString());
                                myReceiptPrinting.WriteLine(strLine);
                                i = j;
                            }
                            else
                            {
                                strLine = string.Format(j + "--{0}",
                                    reservedMemberTable.Rows[i]["strMembershipID"].ToString());
                                myReceiptPrinting.WriteLine(strLine.ToUpper());
                            }
                        }

                    }
                }
                else
                    myReceiptPrinting.WriteLine("Yet to reserve");

                myReceiptPrinting.Print();

            }
        }

