		private void OnVoidReceipt()
		{
			ACMS.XtraUtils.GridViewUtils.UpdateData(GridView12);
			DataRow r = GridView12.GetDataRow(GridView12.FocusedRowHandle);
			
			if (r  != null)
			{
				DialogResult res = MessageBox.Show(this, "Are you sure you want to void this receipt?", "Warning", 
					MessageBoxButtons.YesNo);

				if (res == DialogResult.No) return;

				try
				{
					bool VoidPrevious = false;
					if (employee.HasRight("AB_VOID_RECEIPT_PREVIOUS_DAYS"))
						VoidPrevious = true;
					myPayment.VoidReceipt(r["strReceiptNo"].ToString(),VoidPrevious);
				}
				catch (Exception ex)
