					MessageBox.Show(this, ex.Message);
				}
				finally
				{
					RefreshMemberGrid();
					InitPayment();
					OnGetMemberCreditPackage();
					InitMemberPackage(CurrentMembershipID);
					InitLoyaltyProgram();
					InitMemberActivities(CurrentMembershipID);
				}
			}
		}

