		private void OnEditUnlinkedClassAttendance()
		{
            if (employee.HasRight("AB_UNLINK_PACKAGE"))
            {
                ACMS.XtraUtils.GridViewUtils.UpdateData(GridView10);
                DataRow r = GridView10.GetDataRow(GridView10.FocusedRowHandle);

                if (r != null)
                {
                    string strMembershipID = r["strMembershipID"].ToString();

                    FormUpdateClassAttendance frm = new FormUpdateClassAttendance(strMembershipID, Convert.ToDateTime(r["dtDate"]));

                    DialogResult result = frm.ShowDialog(this);

                    if (result == DialogResult.OK)
                    {
                        int nAttendanceID = ACMS.Convert.ToInt32(r["nAttendanceID"]);
                        try
                        {
                            myMemberPackage.UpdateClassAttendance(nAttendanceID, frm.PackageID);
                            InitMemberPackage(CurrentMembershipID);
                            RefreshUnlinkedPackage();
                        }
                        catch (Exception ex)
                        {
                            MessageBox.Show(this, ex.Message);
                        }
                    }
                }
            }
            else
            {
                MessageBox.Show("You dont have right to Link the class attendance");
                return;
            }
		}

