		private void OnNewPOSTransaction()
		{
			try
			{
				TblShift sqlShift = new TblShift();
				DataTable table = sqlShift.GetCurrentLatestShiftTable(User.BranchCode, terminalUser.NTerminalID);
				
				int nShiftID = -1;
				
				if (table.Rows.Count == 0)
				{
					MessageBox.Show(this, "Please Open a shift first");
					return;
				}
				else 
				{
					nShiftID = ACMS.Convert.ToInt32(table.Rows[0]["MaxShiftNo"]);
					DataTable shiftTable = sqlShift.GetShiftTable(nShiftID, User.BranchCode, terminalUser.NTerminalID);;
					
					if (shiftTable != null && shiftTable.Rows.Count == 1)
					{
						if (shiftTable.Rows[0]["dtCloseTime"] != DBNull.Value)
						{
							MessageBox.Show(this, "Please Open a shift first");
							return;
						}
					}
				}

				ACMS.FormPOS2 frm = new FormPOS2(CurrentMembershipID, User.BranchCode,
                    terminalUser.NTerminalID, User.EmployeeID, nShiftID, lblLocation.Text);
				frm.ShowDialog(this);
				frm.Dispose();

			}
			catch (Exception ex)
			{
				MessageBox.Show(this, ex.Message);
			}
			finally
			{
				RefreshMemberGrid();
				InitPayment();
				OnGetMemberCreditPackage();
				InitMemberPackage(CurrentMembershipID);
				InitLoyaltyProgram();
				InitMemberActivities(CurrentMembershipID);
			}
		}

