        private void OnNewRedemption()
        {
            FormNewRedemption frm = new FormNewRedemption(CurrentMembershipID);
            DialogResult result = frm.ShowDialog(this);

            if (result == DialogResult.OK)
            {
                try
                {
                    int nTransNo=0;
                    myLoyaltyProgram.NewRedemption(currMembershipID, frm.ItemCode, frm.PassFormInformation, ref nTransNo);
                   
                    strSignatureID = "";

                    strKeyData = User.BranchCode.ToString() + "|" + nTransNo + "|" + currMembershipID + "|" + frm.ItemCode + "|1|" + (frm.ItemPoint * frm.PassFormInformation).ToString() + "|" + System.Math.Round(frm.MemberTotalPoint - (frm.ItemPoint * frm.PassFormInformation)).ToString();
                  
                    strDateTime = DateTime.Now.ToString("dd/MM/yyyy  hh:mm:ss tt");
                    string strDateTimeMM = DateTime.Now.ToString("dd/MM/yyyy  hh:mm tt");

                    DigSignature frmSig = new DigSignature(strKeyData, "Redemption", null, null, null);
                    DialogResult result1 = frmSig.ShowDialog();

                    //No Signature not allow to claim Raymond
                    strSignatureID = frmSig.ShowSignature();

                    if (strSignatureID == null || strSignatureID == "")
                    {
                        MessageBox.Show(this, "Member need sign on signature pad for redemption", "Error",MessageBoxButtons.OK,MessageBoxIcon.Error,MessageBoxDefaultButton.Button1);
                        TblRewardsTransaction deleteRewardTrans = new TblRewardsTransaction();
                        deleteRewardTrans.NTransactionID = nTransNo;
                        deleteRewardTrans.Delete();
                        return;
                        /*
                        DialogResult yes = MessageBox.Show(this, "Member haven't signed on signature pad. Confirm to print?", "Warning", MessageBoxButtons.YesNo);
                        if (yes == DialogResult.No)
                            return;*/
                    }
                    string strPdfExportPath = (string)ConfigurationSettings.AppSettings["SaveRewardReceiptPath"].ToString() + "\\" + User.BranchCode + "\\" + CurrentMembershipID.Trim() + "_" + strDateTime.Replace(":", "").Replace(" ", "").Replace("/", "") + ".pdf";

                    TblRewardsTransaction rewardTrans = new TblRewardsTransaction();
                    rewardTrans.NTransactionID = nTransNo;
                    rewardTrans.SelectOne();
                    rewardTrans.StrSignatureID = strSignatureID;
                    rewardTrans.StrSigKey = strKeyData;
                    rewardTrans.StrUtilData = strKeyData;
                    rewardTrans.StrSigPdfPath = strPdfExportPath;
                    rewardTrans.Update();

                    int totalPtsUsed = ((Convert.ToInt32(frm.ItemPoint)) * frm.PassFormInformation);

                    RefreshMemberGrid();
                    InitLoyaltyProgram();
                    PrintRewardTransReceipt(frm.PassFormInformation,frm.ItemCode, frm.ItemPoint, frm.MemberTotalPoint, nTransNo, strSignatureID, strKeyData, strPdfExportPath, strDateTimeMM);
                }
                catch (Exception ex)
                {
                    MessageBox.Show(this, ex.Message);
                }
            }
        }

