		private void OnNewClassAttendance(string membershipID)
		{
			ACMS.XtraUtils.GridViewUtils.UpdateData(GridView17);

			if (NClassInstanceID == -1)
				return;
		
			DataRow row = GridView17.GetDataRow(GridView17.FocusedRowHandle);	
			if (row != null)
			{
				DateTime classDate = ACMS.Convert.ToDateTime(row["dtDate"]);
				if (DateTime.Compare(classDate.Date, DateTime.Now.Date) < 0)
				{
					employee.RightsLevel.Id = 30;
					if (!employee.HasRight("AB_MARK_ATTENDANCE_PREVIOUS_DAY"))
					{
						MessageBox.Show(this, "You are not authorised to mark previous day attendance.");
						return;
					}
				}
				else if (DateTime.Compare(classDate.Date, DateTime.Now.Date) > 0)
				{
					MessageBox.Show(this, "You are not authorised to mark future date.");
					return;
				}
				else if (row["NVerifyID"] == DBNull.Value)
				{
					MessageBox.Show(this, "All classes must be verify by Instructor/Staff.");
					return;
				}
			}

			ACMSDAL.TblClassAttendance sqlClassAtt = new TblClassAttendance();
			DataRow classAttendanceRow = null;
		
			bool isAttendedNow = sqlClassAtt.IsAttendedThisClass(NClassInstanceID, membershipID, ref classAttendanceRow);
			
			if (isAttendedNow)
			{
				int nStatusID = ACMS.Convert.ToInt32(classAttendanceRow["nStatusID"]);

				if (nStatusID == 1)
				{
					// Process
					throw new Exception("Member already been marked in this class.");
				}

				try
				{
					myClassAttendance.MarkAttendance(NClassInstanceID, -1, 
						membershipID, 0, true, false);
				}
				catch (Exception ex)
				{
					MessageBox.Show(this, ex.Message);
				}
				finally
				{
					GetClassAttendance();
					GetClassReservation();
				}

			}
			else
			{
				FormNewClassAttendanceInClassAttendanceModule frm;

				if (membershipID == "")
					frm = new FormNewClassAttendanceInClassAttendanceModule(false);
				else
					frm = new FormNewClassAttendanceInClassAttendanceModule(membershipID, false);

				DialogResult result = frm.ShowDialog(this);

				if (result == DialogResult.OK)
				{
					try					
					{
                        DataSet ds = new DataSet();

                        string strSQL = "select npackageid from tblMemberPackage mp left join tblReceipt r on mp.strReceiptNo=r.strReceiptNo left join tblPackage p on mp.strPackageCode=p.strPackageCode " +
                                        "where mp.strReceiptNo is not null and r.strReceiptNo is null and nStatusID=0 and mp.strRemarks not like '%PKG MISSING FROM SYSTEM%' " +
                                        "and mp.strReceiptNo not like '%FOC%' and fFree=0 and p.nCategoryID not in (23,14) and nPackageID=" + frm.MemberPackageID;
                        SqlHelper.FillDataset(connection, CommandType.StoredProcedure, "UP_GETDATA", ds, new string[] { "Table" }, new SqlParameter("@strSQL", strSQL));

                        if (ds.Tables[0].Rows.Count > 0)
                        {
                            MessageBox.Show("Not allow to use this package as not receipt found.", "Warning", MessageBoxButtons.OK);
                            return;
                        }

                        if (PackageHasOutStanding(frm.MemberPackageID))
                        {
                            double usedAmount;
                            double paidAmount;

                            TblMemberPackage sqlMemberPackage = new TblMemberPackage();
                            TblMember sqlMember = new TblMember();

                            sqlMember.StrMembershipID = frm.MembershipID;

                            DataTable memberPackageBalanceTable = sqlMemberPackage.GetMemberPackage(frm.MemberPackageID);
                            DataTable memberTable = sqlMember.SelectOne();

                            myMemberPackage.CalculateMemberPackageBalance(frm.MembershipID, memberPackageBalanceTable);

                            usedAmount = ACMS.Convert.ToDouble(memberPackageBalanceTable.Rows[0]["usedAmt"]);
                            paidAmount = ACMS.Convert.ToDouble(memberPackageBalanceTable.Rows[0]["paidAmt"]);

                            if (usedAmount > paidAmount*0.7)
                            {
                                MessageBox.Show(memberTable.Rows[0]["strMemberName"].ToString() + " had attended sessions value is more than the paid amount.\n\nPlease pay outstading before proceed!", "Warning", MessageBoxButtons.OK);
                                
                                return;
                            }
                        }
						myClassAttendance.MarkAttendance(NClassInstanceID, frm.MemberPackageID, 
							frm.MembershipID, 0, frm.Refunded, frm.IsNeedToVerifyMemberPackage);
					}
					catch (Exception ex)
					{
						MessageBox.Show(this, ex.Message);
					}
					finally
					{
						GetClassAttendance();
						GetClassReservation();
					}
				}	
			}
		}

