                    {
                        MessageBox.Show(this, "You are not allow to mark previous day attendance.");
                        return;
                    }
                }
                else if (DateTime.Compare(classDate.Date, DateTime.Now.Date) > 0)
                {
                    MessageBox.Show(this, "You are not allow to mark future day attendance.");
                    return;
                }
                else if (row["NVerifyID"] == DBNull.Value)
                {
                    MessageBox.Show(this, "You are not allow to mark the class that no yet verify.");
                    return;
                }
            }

            TblMember sqlMember = new TblMember();
            if (!sqlMember.MembershipIDExists(membershipID))
            {
                MessageBox.Show(this, "Invalid Membership ID!");
                return;
            }

            ACMSDAL.TblMemberPackage memberPackage = new ACMSDAL.TblMemberPackage();
