                        double paidAmount;                    
                                      
                        sqlMember.StrMembershipID = foundRows[0]["strMembershipID"].ToString();

                        DataTable memberTable = sqlMember.SelectOne();
                        myMemberPackage.CalculateMemberPackageBalance(foundRows[0]["strMembershipID"].ToString(), tableMemberPackage);
                                                                    
                        foundRows = tableMemberPackage.Select("dtExpiryDate IS NOT NULL", "dtExpiryDate");
                                                                   
                        usedAmount = ACMS.Convert.ToDouble(foundRows[0]["usedAmt"]);
                        paidAmount = ACMS.Convert.ToDouble(foundRows[0]["paidAmt"]);

                        if (usedAmount > paidAmount*0.7)
                        {
                            MessageBox.Show(memberTable.Rows[0]["strMemberName"].ToString() + " had attended total sessions' value is more than the paid amount.\n\nPlease pay outstading before proceed!", "Warning", MessageBoxButtons.OK);
                            //MessageBoxButtons.YesNo);

                            //if (result == DialogResult.No)
                            return;
                        }
                    }
					
                }
                else
                {
                    OnNewClassAttendance(membershipID);
                    txtEdtMembershipIDToMark.Focus();
                    return;
                }
            }
            else if (tableMemberPackage.Rows.Count == 0)
            {
                DataSet _ds = new DataSet();
                //Check any active holistic package owned by member
                string strSQL = " select nCreditPackageID from tblMemberCreditPackage mcp, tblCreditPackage cp where strMembershipID='" + membershipID + "' and nStatusID=0 " +
                    "and mcp.strCreditPackageCode = cp.strCreditPackageCode and nCategoryID IN (36,37) ";

