		private void OnDeleteClassAttendance_ServiceSession()
		{
            //SimpleButton21 and SimpleButton52


            DataRow row1 = GridView7.GetDataRow(GridView7.FocusedRowHandle);
            if (row1 != null)
            {
                if (ACMS.Convert.ToDBInt32(row1["nStatusID"]) == 3)
                {
                    MessageBox.Show("You cant Delete Converted Package");
                    return;
                }

                if (ACMS.Convert.ToDBInt32(row1["nStatusID"]) == 4)
                {
                    MessageBox.Show("You cant Delete Refunded Package");
                    return;
                }
            }

			if (xtraTabPage1.PageVisible && !xtraTabPage2.PageVisible)
			{
				ACMS.XtraUtils.GridViewUtils.UpdateData(GridView8);
				DataRow row = GridView8.GetDataRow(GridView8.FocusedRowHandle);

				if (row != null)
				{
                    if (DateTime.Compare(System.Convert.ToDateTime(row["dtDate"]), DateTime.Now.Date) > 0)
                      {
                        employee.RightsLevel.Id = 13;
                        if (!employee.HasRight("AB_DELETE_ATTENDANCE"))
                        {
                            MessageBox.Show(this, "You are not allow to perform this action.");
                            return;
                        }
                      }
                      else if (DateTime.Compare(System.Convert.ToDateTime(row["dtDate"]), DateTime.Now.Date) < 0)
                      {
                          if (!employee.HasRight("AB_DELETE_PREVIOUS_ATTENDANCE"))
                          {
                              MessageBox.Show(this, "You cant delete previous attendance.");
                              return;
                          }
                      }
					int nAttendanceID = ACMS.Convert.ToInt32(row["nAttendanceID"]);
					int nPackageID = ACMS.Convert.ToInt32(row["nPackageID"]);
					
					FormCancelClassAttendance frm = new FormCancelClassAttendance(nAttendanceID, MembershipCurrentMemberName);
				
					DialogResult result = frm.ShowDialog(this);

					if (result == DialogResult.OK)
					{
						try
						{
							myMemberPackage.DeleteClassAttendance(nPackageID, nAttendanceID, frm.Remark);
							InitMemberPackage(CurrentMembershipID);
							OnMemberPackageGridFocusRowChanged();
