						catch (Exception ex)
						{
							MessageBox.Show(ex.Message);
						}
					}
				}
			}
			else
			{
				ACMS.XtraUtils.GridViewUtils.UpdateData(gridView4);
				DataRow row = gridView4.GetDataRow(gridView4.FocusedRowHandle);
                int nDeleteMonth = System.Convert.ToDateTime(row["dtDate"]).Month;
                int nDeleteYear = System.Convert.ToDateTime(row["dtDate"]).Year;
                DateTime dtNow = DateTime.Now;
				if (row != null)
				{
					employee.RightsLevel.Id = 14;
					if (!employee.HasRight("AB_DELETE_SERVICE_SESSION"))
					{
						MessageBox.Show(this, "You are not allow to perform this action.");
						return;
					}
                    else if (!employee.HasRight("AB_DELETE_PREVIOUS_SERVICE_SESSION") && (dtNow.Month != nDeleteMonth || dtNow.Year != nDeleteYear))
                    {
                        MessageBox.Show(this, "You are not allow to Delete previous month Service.");
                        return;
                    }

					int nAttendanceID = ACMS.Convert.ToInt32(row["nSessionID"]);
					int nPackageID = ACMS.Convert.ToInt32(row["nPackageID"]);

                    FormCancelClassAttendance frm = new FormCancelClassAttendance(nAttendanceID, MembershipCurrentMemberName);

                    DialogResult result = frm.ShowDialog(this);

                    if (result == DialogResult.OK)
                    {
                        try
                        {
                            myMemberPackage.DeleteServiceSession(nPackageID, nAttendanceID, frm.Remark);
                            InitMemberPackage(CurrentMembershipID);
                            OnMemberPackageGridFocusRowChanged();
                        }
                        catch (Exception ex)
                        {
                            MessageBox.Show(ex.Message);
                        }
                    }
				}
			}
		}

