		private void OnNewPackageExtension()
		{
            int iReasonID = 0;
            Boolean bCheck = false;
            Boolean bBatch = false;
            DateTime? dtNewExpiryDate = null;
            DateTime? dtStartDate = null;
            DateTime? dtEndDate = null;
            try
            {
                //to support extension for multiple package at once
                for (int i = 0; i < GridView7.RowCount; i++)
                {
                    int rowHandle = GridView7.GetVisibleRowHandle(i);
                    DataRow row = GridView7.GetDataRow(rowHandle);

                    if (GridView7.IsDataRow(rowHandle))
