                            }

                            if (ACMS.Convert.ToDBInt32(row["nStatusID"]) == 4)
                            {
                                MessageBox.Show("You cant Extend Refunded Package");
                                return;
                            }

                            if (row["dtExpiryDate"].ToString() == "")
                            {
                                MessageBox.Show(this, "Package not in use yet");
                                return;
                            }

                            ACMSDAL.TblMemberPackage sqlGpCalcPackages = new ACMSDAL.TblMemberPackage();
                            bool bGpGiro = sqlGpCalcPackages.IsPackageMemberGIROPackage(ACMS.Convert.ToDBInt32(row["nPackageID"]));
                            int iPackageID = ACMS.Convert.ToDBInt32(row["nPackageID"]);
                            //FormNewMemberPackageExtension frmGp = new FormNewMemberPackageExtension(myMemberPackage, ACMS.Convert.ToDBInt32(row["nPackageID"]), bGpGiro, row);
                            FormNewMemberPackageExtension frmGp = new FormNewMemberPackageExtension(myMemberPackage, iPackageID, bGpGiro, row);
                                                        
                            if (bBatch == false)
                            {
                                DialogResult resultGp = frmGp.ShowDialog();

                                if (resultGp == DialogResult.OK)
                                {
                                    try
                                    {
                                        employee.RightsLevel.Id = 8;
                                        if (!employee.HasRight("AB_EXTEND_PACKAGE_USING_OTHERS_AS_TYPE"))
                                        {
                                            MessageBox.Show(this, "You are not authorise to perform package extension.");
                                            return;
                                        }

                                        dtNewExpiryDate = frmGp.NewExpiryDate;
                                        dtStartDate = frmGp.StartDate;
                                        dtEndDate = frmGp.EndDate;
                                        iReasonID = frmGp.NLeaveTypeID;


                                        myMemberPackage.SaveExtensionHistory(iPackageID, frmGp.PackageExtensionTable, frmGp.NewExpiryDate, bGpGiro, frmGp.StartDate, frmGp.EndDate, iReasonID);
                                        //InitMemberPackage(CurrentMembershipID);
                                        //OnMemberPackageGridFocusRowChanged();
                                        bBatch = true;
                                    }
                                    catch (Exception ex)
                                    {
                                        MessageBox.Show(this, ex.Message);
                                    }
                                }
                            }
                            else
                            {
                                try
                                {
                                    employee.RightsLevel.Id = 8;
                                    if (!employee.HasRight("AB_EXTEND_PACKAGE_USING_OTHERS_AS_TYPE"))
                                    {
                                        MessageBox.Show(this, "You are not authorise to perform package extension.");
                                        return;
                                    }

                                    myMemberPackage.SaveExtensionHistory(iPackageID, frmGp.PackageExtensionTable, frmGp.NewExpiryDate, bGpGiro, Convert.ToDateTime(dtStartDate), Convert.ToDateTime(dtEndDate), iReasonID);
                                    //InitMemberPackage(CurrentMembershipID);
                                    //OnMemberPackageGridFocusRowChanged();
                                }
                                catch (Exception ex)
                                {
                                    MessageBox.Show(this, ex.Message);
                                }
                            }
                        }
                    }
                }

                if (bBatch == true)
                {
                    InitMemberPackage(CurrentMembershipID);
                    OnMemberPackageGridFocusRowChanged();
                }

                //normal flow if grid view checkbox not being checked
                if (bCheck == false)
                {
                    DataRow dr = GridView7.GetDataRow(GridView7.FocusedRowHandle);

                    if (ACMS.Convert.ToDBInt32(dr["nStatusID"]) == 3)
                    {
                        MessageBox.Show("You cant Extend Converted Package");
                        return;
                    }

                    if (ACMS.Convert.ToDBInt32(dr["nStatusID"]) == 4)
                    {
                        MessageBox.Show("You cant Extend Refunded Package");
                        return;
                    }

                    if (dr["dtExpiryDate"].ToString() == "")
                    {
                        MessageBox.Show(this, "Package not in use yet");
                        return;
                    }
                    //10-12-2012

                    ACMSDAL.TblMemberPackage sqlCalcPackages = new ACMSDAL.TblMemberPackage();
                    bool bGiro = sqlCalcPackages.IsPackageMemberGIROPackage(MemberPackagePackageID);

                    //
                    FormNewMemberPackageExtension frm = new FormNewMemberPackageExtension(myMemberPackage, MemberPackagePackageID, bGiro, dr);
                    DialogResult result = frm.ShowDialog();

                    if (result == DialogResult.OK)
                    {
                        try
                        {
                            //					if (frm.NLeaveTypeID == 0)
                            //					{
                            employee.RightsLevel.Id = 8;
                            if (!employee.HasRight("AB_EXTEND_PACKAGE_USING_OTHERS_AS_TYPE"))
                            {
                                MessageBox.Show(this, "You are not authorise to perform package extension.");
                                return;
                            }
                            //					}

                            myMemberPackage.SaveExtensionHistory(MemberPackagePackageID, frm.PackageExtensionTable, frm.NewExpiryDate, bGiro, frm.StartDate, frm.EndDate, iReasonID);
                            InitMemberPackage(CurrentMembershipID);
                            OnMemberPackageGridFocusRowChanged();
                        }
                        catch (Exception ex)
                        {
                            MessageBox.Show(this, ex.Message);
                        }
                    }
                }
            }
            catch (Exception ex)
            {
            }
            finally
            {
                dtNewExpiryDate = null;
                dtStartDate = null;
                dtEndDate = null;
                iReasonID = 0;
            }
		}

