        private void OnUpdatePTTherapist()
        {
            DataRow row = gridView4.GetDataRow(gridView4.FocusedRowHandle);

            try
            {
                if (row != null)
                {
                    int nDeleteMonth = System.Convert.ToDateTime(row["dtDate"]).Month;
                    int nDeleteYear = System.Convert.ToDateTime(row["dtDate"]).Year;
                    int nSessionID = Convert.ToInt32(row["nSessionID"]);
                    TblServiceSession ss = new TblServiceSession();
                    ss.NSessionID = nSessionID;
                    ss.SelectOne();
                    DateTime dtNow = DateTime.Now;
                    int nNewServiceEmployeeID = Convert.ToInt32(row["nServiceEmployeeID"]);
                    TblEmployee emp = new TblEmployee();
                    emp.NEmployeeID = nNewServiceEmployeeID;
                    emp.SelectOne();
                    if (emp.NStatusID.ToString() != "True")
                    {
                        //DialogResult yesno = MessageBox.Show(this, string.Format("ID "+ nNewServiceEmployeeID.ToString() + " selected belongs to resigned staff. Confirm to proceed ?", "", MessageBoxButtons.OKCancel));
                        DialogResult yes = MessageBox.Show(this, "ID " + nNewServiceEmployeeID.ToString() + " selected belongs to resigned staff. Confirm to proceed ?", "Warning", MessageBoxButtons.YesNo);
                        if (yes == DialogResult.No)
                        {                        
                            this.DialogResult = DialogResult.None;
                            return;
                        }
                    }
                    DateTime dtNewTreatmentDate = Convert.ToDateTime(row["dtTreatment"]);
                    DateTime dtNewDate = Convert.ToDateTime(row["dtDate"]);
                    employee.RightsLevel.Id = 16;
                    if (!employee.HasRight("AB_EDIT_SERVICE_SESSION"))
                    {
                        MessageBox.Show(this, "You are not allow to perform this action.");
                        return;
                    }
                    else if (!employee.HasRight("AB_EDIT_PREVIOUS_SERVICE_SESSION") && (dtNow.Month != nDeleteMonth || dtNow.Year != nDeleteYear || dtNewDate != ss.DtDate || dtNewTreatmentDate != ss.DtTreatment))
                    {
                        MessageBox.Show(this, "You are not allow to Edit previous month Service.");
                        return;
                    }
                    else if (!employee.HasRight("AB_EDIT_GIFT_TO_FRIENDS") && (dtNewDate != ss.DtDate || dtNewTreatmentDate != ss.DtTreatment) && ss.NServiceEmployeeID==9999)
                    {
                        MessageBox.Show(this, "You are not allow to Edit Gift To Friends' service.");
                        return;
                    }
                    myMemberPackage.UpdateGiftToFriends(nSessionID, nNewServiceEmployeeID, dtNewDate, dtNewTreatmentDate);
                    MessageBox.Show(this, "Attendance / Service record updated.");
                }                
                //InitMemberPackage(CurrentMembershipID);
                //OnMemberPackageGridFocusRowChanged();
            }
            catch (Exception ex)
            {
                MessageBox.Show(this, ex.Message);
            }
        }        
        
