        private void OnNewClassReservationUseScanner(string strMembershipID)
        {
            ACMS.XtraUtils.GridViewUtils.UpdateData(GridView17);

            if (NClassInstanceID == -1)
                return;

            TblMember sqlMember = new TblMember();
            if (!sqlMember.MembershipIDExists(strMembershipID))
            {
                MessageBox.Show(this, "Invalid Membership ID!");
                return;
            }

            //Checking if Member Already have an attendance record (Reserved, Forfeited or Processed) for this class. - avoid duplication
            DataRow myrow = GridView17.GetDataRow(GridView17.FocusedRowHandle);
            if (myrow != null)
            {
                DateTime classDate = ACMS.Convert.ToDateTime(myrow["dtDate"]);

                TblClassAttendance classAttendance = new TblClassAttendance();
                if (classAttendance.IsReservedThisClass(NClassInstanceID, strMembershipID, classDate))
                {
                    MessageBox.Show(this, "This member already have an attendance record (Reserved, Forfeited or Processed) for this class.");
                    return;
                }
            }

            ACMSDAL.TblMemberPackage memberPackage = new ACMSDAL.TblMemberPackage();
            DataTable tableMemberPackage = memberPackage.GetAllMemberPackageForClassAttendance(strMembershipID);
            myMemberPackage.CalculateMemberPackageBalance(strMembershipID, tableMemberPackage);           
           
            foreach (DataRow dr in tableMemberPackage.Rows)
            {
                if (!myClassAttendance.VerifyMemberPackageAllowClass(Convert.ToInt32(dr["nPackageID"]), NClassInstanceID))
                    dr.Delete();
                else if (Convert.ToInt32(dr["Balance"]) <= 0)
                    dr.Delete();
            }
            tableMemberPackage.AcceptChanges();

            int memberPackageID = -1;
            // if i can pass unLinked Package ID to method, then needtoverify is false
            // if it is a normaly member package that selected from lookupedit, then needtoverify = true
            bool needToVerifyMemberPackage = true;

            if (tableMemberPackage.Rows.Count > 1)
            {
                bool hasActive;

                DataRow[] foundRows;
                hasActive = (Convert.ToInt16(tableMemberPackage.Compute("COUNT(nPackageID)", "dtExpiryDate IS NOT NULL")) > 0);

                if (hasActive)
                {
                    foundRows = tableMemberPackage.Select("dtExpiryDate IS NOT NULL", "dtExpiryDate");
                    memberPackageID = ACMS.Convert.ToInt32(foundRows[0]["nPackageID"]);


                    if (PackageHasOutStanding(Convert.ToInt32(foundRows[0]["nPackageID"])))
                    {
                        double usedAmount;
                        double paidAmount;

                        sqlMember.StrMembershipID = foundRows[0]["strMembershipID"].ToString();

                        DataTable memberTable = sqlMember.SelectOne();
                        myMemberPackage.CalculateMemberPackageBalance(foundRows[0]["strMembershipID"].ToString(), tableMemberPackage);


                        foundRows = tableMemberPackage.Select("dtExpiryDate IS NOT NULL", "dtExpiryDate");


                        usedAmount = ACMS.Convert.ToDouble(foundRows[0]["usedAmt"]);
                        paidAmount = ACMS.Convert.ToDouble(foundRows[0]["paidAmt"]);


                        if (usedAmount > paidAmount * 0.7)
                        {
                            MessageBox.Show(memberTable.Rows[0]["strMemberName"].ToString() + " had attended total sessions' value is more than 70% of the paid amount.\n\nPlease pay outstading before proceed!", "Warning", MessageBoxButtons.OK);
                            //MessageBoxButtons.YesNo);

                            //if (result == DialogResult.No)
                            return;
                        }
                    }

                }
                else
                {
                    OnNewClassReservation(strMembershipID);
                    txtEdtReservationMemberIDScan.Focus();
                    return;
                }
            }
            else if (tableMemberPackage.Rows.Count == 0)
            {
                if (strMembershipID.Contains("NMC"))
                {
                    string strSQL;
                    DataSet _ds;

                    strSQL = "select dtdate from tblClassAttendance where strMembershipID ='" + strMembershipID + "' order by dtDate desc";
                    _ds = new DataSet();
                    SqlHelper.FillDataset(connection, CommandType.StoredProcedure, "UP_GETDATA", _ds, new string[] { "table" }, new SqlParameter("@strSQL", strSQL));

                    if (_ds.Tables[0].Rows.Count > 0)
                    {
                        if (Convert.ToDateTime(_ds.Tables[0].Rows[0]["dtDate"]).AddDays(30) > DateTime.Now.Date && Convert.ToDateTime(_ds.Tables[0].Rows[0]["dtDate"]).ToString("dd/MM/yyyy") != DateTime.Now.Date.ToString("dd/MM/yyyy"))
                        {
                            DialogResult result = MessageBox.Show(strMembershipID + " already attended free class on " + Convert.ToDateTime(_ds.Tables[0].Rows[0]["dtDate"]).ToString("d/M/yyyy") + "\n Non member only entitled one free class within a month. \nDo you still want to mark it. \n " +
                                "(An unlinked record will be create if you click yes) ", "Warning", MessageBoxButtons.YesNo);
                            if (result == DialogResult.Yes)
                            {
                                ACMSLogic.MemberPackage.CreateUnlinkedMemberPackage(strMembershipID, ref memberPackageID);
                                needToVerifyMemberPackage = false;
                            }
                            else
                            {
                                txtEdtReservationMemberIDScan.Text = "";
                                txtEdtReservationMemberIDScan.Focus();
                                return;
                            }
                        }
                    }
                    _ds.Dispose();
                }
                else
                {                    
                    DialogResult result = MessageBox.Show("No package found. Do you still want to reserve it. \n " +
                    "(An unlinked record will be create if you click yes) ", "Warning", MessageBoxButtons.YesNo);

                    if (result == DialogResult.Yes)
                    {
                        ACMSLogic.MemberPackage.CreateUnlinkedMemberPackage(strMembershipID, ref memberPackageID);
                        needToVerifyMemberPackage = false;
                    }
                    else
                    {
                        txtEdtReservationMemberIDScan.Text = "";
                        txtEdtReservationMemberIDScan.Focus();
                        return;
                    }
                }
            }
            else if (tableMemberPackage.Rows.Count == 1)
            {
                if (PackageHasOutStanding(Convert.ToInt32(tableMemberPackage.Rows[0]["nPackageID"])))
                {
                    double usedAmount;
                    double paidAmount;

                    sqlMember.StrMembershipID = tableMemberPackage.Rows[0]["strMembershipID"].ToString();
                    DataTable memberTable = sqlMember.SelectOne();
                    myMemberPackage.CalculateMemberPackageBalance(tableMemberPackage.Rows[0]["strMembershipID"].ToString(), tableMemberPackage);

                    usedAmount = ACMS.Convert.ToDouble(tableMemberPackage.Rows[0]["usedAmt"]);
                    paidAmount = ACMS.Convert.ToDouble(tableMemberPackage.Rows[0]["paidAmt"]);

                    if (usedAmount > paidAmount * 0.7)
                    {
                        MessageBox.Show(memberTable.Rows[0]["strMemberName"].ToString() + " had attended total sessions' value is more than the paid amount.\n\nPlease pay outstading before proceed!", "Warning", MessageBoxButtons.OK);
                        //MessageBoxButtons.YesNo);

                        //if (result == DialogResult.No)
                            return;
                    }
                }
                memberPackageID = ACMS.Convert.ToInt32(tableMemberPackage.Rows[0]["nPackageID"]);
            }

            try
            {
                myClassAttendance.NormalReserve(strMembershipID,
                    memberPackageID, NClassInstanceID, needToVerifyMemberPackage);
            }
            catch (Exception ex)
            {
                MessageBox.Show(ex.Message);
            }
            finally
            {
                GetClassReservation();
                txtEdtMembershipIDToMark.Text = "";
                txtEdtMembershipIDToMark.Focus();
            }
        }

