		private void OnInstructorLogin()
		{
			ACMS.XtraUtils.GridViewUtils.UpdateData(GridView17);

			if (NClassInstanceID == -1)
				return;

			TblClassInstance sqlClassInstance = new TblClassInstance();
			sqlClassInstance.NClassInstanceID = NClassInstanceID;
			sqlClassInstance.SelectOne();

			if (sqlClassInstance.DtDate.Value.Date > DateTime.Today.Date)
			{
				MessageBox.Show(this, "You cannot login to future class as an instructor.");
				return;
			}

			if (!sqlClassInstance.NActualInstructorID.IsNull)
			{
				MessageBox.Show(this, "The class has been login by other intructor.");
				return;
			}
					
			if (sqlClassInstance.NPermanentInstructorID.IsNull && sqlClassInstance.NReplacementInstructorID.IsNull)
			{
				MessageBox.Show(this, "The class no has either permanent and replacement instructor.");
				return;
			}

			int loginID = sqlClassInstance.NReplacementInstructorID.IsNull ?
				ACMS.Convert.ToInt32(sqlClassInstance.NPermanentInstructorID) : sqlClassInstance.NReplacementInstructorID.Value;

			InstructorLogin login = new InstructorLogin(loginID);
			DialogResult result = login.ShowDialog(this);

			if (result == DialogResult.OK)
			{
				try
				{
					if (myClassAttendance.LoginClassInstance(NClassInstanceID, login.UserID, login.Password))
					{
						RefreshClassAttendance();

						MessageBox.Show(this,  login.UserID +  " is been login to class ID" + NClassInstanceID.ToString());
					}
					else 
						MessageBox.Show(this, login.UserID + " is failed to login to class ID. Member ID or Password is wrong." + NClassInstanceID.ToString() );
				}
				catch (Exception ex)
				{
					MessageBox.Show(this, ex.Message); 
				}
			}
		}

		
