		private void OnStockRequest_Print2()
		{
			if (GridView30.FocusedRowHandle < 0)
			{
				UI.ShowErrorMessage(this, "Please select a record a Stock Request.");
				return;
			}

			int nStockRequestNo = ACMS.Convert.ToInt32(GridView30.GetDataRow(GridView30.FocusedRowHandle)["nStockRequestNo"]);
			string query=@" select a.dtDate as Date,a.nStockRequestNo as RequestNo,b.strBranchCode as FromBranch,
							c.strBranchCode as ToBranch,d.strEmployeeName as RequestedBy,case a.nstatusid when '0' then 'Pending'
							when '1' then 'Process' when '2' then 'Rejected' END AS Status, a.strRemarks as Remark
							from tblStockRequest a inner join tblBranch b
							on a.strBranchCodeFrom=b.strBranchCode inner join tblBranch c
							on a.strBranchCodeTo=c.strBranchCode inner join tblEmployee d
							on a.nEmployeeId=d.nEmployeeId where a.nStockRequestNo="
				+nStockRequestNo
				+";";
			query+=@" select a.nStockRequestNo as RequestNo,a.strItemCode as ItemCode,b.strDescription as Description,
					a.nQuantity as Quantity, b.strBrandCode as Brand, b.strStyleCode as Style, b.strColorCode as Color, 
					b.strSizeCode as Size from tblStockRequestEntries a inner join tblProduct b on a.strItemCode=b.strProductCode 
					where a.nStockRequestNo="
				+nStockRequestNo;

			DataSet dsResult = SqlHelper.ExecuteDataset(SqlHelperUtils.connectionString, CommandType.Text, query);
			dsResult.Tables[0].TableName = "StockRequest";
			dsResult.Tables[1].TableName = "StockRequestEntries";
			dsResult.Relations.Add("StockRequestRelation", dsResult.Tables["StockRequest"].Columns["RequestNo"],
				dsResult.Tables["StockRequestEntries"].Columns["RequestNo"], true);

			ACMS.ACMSBranch.Printing.rptStockRequest rpt = new ACMS.ACMSBranch.Printing.rptStockRequest();
			rpt.DataSource = dsResult;
			//rpt.RunDesigner();
			rpt.Print();
		}

