		private void OnIBT_Print2()
		{
			if (GridView31.FocusedRowHandle < 0)
			{
				UI.ShowErrorMessage(this, "Please select a record a Inter Branch Transfer.");
				return;
			}

			int nIBTNo = ACMS.Convert.ToInt32(GridView31.GetDataRow(GridView31.FocusedRowHandle)["nIBTNo"]);

			string query=@" select a.dtDate as Date,a.nIBTNo,b.strBranchCode as FromBranch, a.strRemarks as Remark,
							c.strBranchCode as ToBranch,d.strEmployeeName as RequestedBy, 
							case a.nstatusid when '0' then 'Pending Receipt'
							when '1' then 'Received' when '2' then 'Cancelled' END AS Status
							from tblIBT a inner join tblBranch b
							on a.strBranchCodeFrom=b.strBranchCode inner join tblBranch c
							on a.strBranchCodeTo=c.strBranchCode inner join tblEmployee d
							on a.nEmployeeId=d.nEmployeeId where a.nIBTNo="
				+nIBTNo
				+";";
			query+=@" select a.nIBTNo, a.strItemCode as ItemCode,b.strDescription as Description, a.nQuantity as Quantity, 
					b.strBrandCode as Brand, b.strStyleCode as Style, b.strColorCode as Color, b.strSizeCode as Size 
					from tblIBTEntries a inner join tblProduct b on a.strItemCode=b.strProductCode where a.nIBTNo="
				+nIBTNo;

			DataSet dsResult = SqlHelper.ExecuteDataset(SqlHelperUtils.connectionString, CommandType.Text, query);
			dsResult.Tables[0].TableName = "IBT";
			dsResult.Tables[1].TableName = "IBTEntries";
			dsResult.Relations.Add("IBTRelation", dsResult.Tables["IBT"].Columns["nIBTNo"],
				dsResult.Tables["IBTEntries"].Columns["nIBTNo"], true);

			ACMS.ACMSBranch.Printing.rptIBT rpt = new ACMS.ACMSBranch.Printing.rptIBT();
			rpt.DataSource = dsResult;
			//rpt.RunDesigner();
			rpt.Print();
		}
