		private void btnEditPaymentMode_Click(object sender, System.EventArgs e)
		{
			DataRow drShift = this.gridViewShift.GetDataRow(gridViewShift.FocusedRowHandle);

			if (gridViewReceipt.FocusedRowHandle >= 0) 
			{
				DataRow dr = gridViewReceipt.GetDataRow(gridViewReceipt.FocusedRowHandle);
				
				if (dr == null)
					return;

				DataSet _ds = new DataSet();
				string strSQL;
				
				//if (employee.HasRight("AB_EDIT_BRANCH_MODE_SHIFT")==false)
					//				strSQL = "up_Get_Past_ReceiptShiftVerify " + drShift["nTerminalID"] + "," + drShift["nShiftID"] + ",'" + string.Format("{0:MM/dd/yyyy}",Convert.ToDateTime(drShift["dtDate"])) + "','" + drShift["strBranchCode"] + "'";				
					//				else
					strSQL = "up_Get_ReceiptShiftVerify " + drShift["nTerminalID"] + "," + drShift["nShiftID"] + ",'" + string.Format("{0:MM/dd/yyyy}",Convert.ToDateTime(drShift["dtDate"])) + "','" + drShift["strBranchCode"] + "'";
				
					SqlHelper.FillDataset(connection,CommandType.StoredProcedure,"UP_GETDATA",_ds,new string[] {"Table"}, new SqlParameter("@strSQL", strSQL) );
			
					DataTable dt = _ds.Tables["Table"];

					if (dt.Rows.Count > 0 && employee.HasRight("AB_EDIT_BRANCH_MODE_SHIFT")==false)
					{
						MessageBox.Show("This shift has been verified.You are not allowed to change payment mode.","Payment Mode");
						return;
					}
				
				
				_ds = new DataSet();

				strSQL = "Select * From TblReceiptPayment Where strReceiptNo = '" + dr["strReceiptNo"] + "' And nIPPID is not null";

				SqlHelper.FillDataset(connection,CommandType.StoredProcedure,"UP_GETDATA",_ds,new string[] {"Table"}, new SqlParameter("@strSQL", strSQL) );
			
				dt = _ds.Tables["Table"];
				
				if (dt.Rows.Count >0)
				{
					MessageBox.Show("This shift has been moved to IPP.You are not allowed to change payment mode.","Payment Mode");
					return;
				}

				ACMS.ACMSBranch.BranchTransaction.frmPaymentCode frmPaymentCode = new ACMS.ACMSBranch.BranchTransaction.frmPaymentCode(dr["strReceiptNo"].ToString());
				frmPaymentCode.ShowDialog();
				frmPaymentCode.Dispose();
				LoadShift();
				LoadReceipt();
			}
		}
