		private void SimpleButton20_Click(object sender, System.EventArgs e)
		{
			//Derek - 130920 - Pop Up Package Selection List
            //Retrieve List of Package IDs that need utilization
            //Loop 1 by 1 pop up Utilization forms for Class Attendance, PT (3) and Spa Services (4, 5, 6, 34)
            //Save Utilization Slips for PT (3) and Spa Services (5, 6, 34) only
            //Option to print or print later
            //Option to recapture signature afterwards - searches by PDF Loc string - get nSessionIDs and nPackageIDs
            //If exist in tblMemberPackage table then CreditAccJoin.cs
            //Else if only exist in tblServiceSession table and count = 1 then PackageAccountRpt.cs
            //Else if only exist in tblServiceSession table and count > 1 then PackageAccountJoinRpt.cs                   

            bool IsUtlBoxChecked = false;
            bool IsMixedCatSelected = false;
            int curDivision = 0;
            int prevDivision = 0;

            TblMemberPackage myTMP = new TblMemberPackage();

            DataTable dtSelectedPackages = new DataTable();
            dtSelectedPackages.Columns.Add("UtlCheck", typeof(string));
            dtSelectedPackages.Columns.Add("nPackageID", typeof(int));
            dtSelectedPackages.Columns.Add("strMembershipID", typeof(string));
            dtSelectedPackages.Columns.Add("strPackageCode", typeof(string));
            dtSelectedPackages.Columns.Add("strDescription", typeof(string));
            dtSelectedPackages.Columns.Add("nStatusID", typeof(int));
            dtSelectedPackages.Columns.Add("nCategoryID", typeof(int));
            dtSelectedPackages.Columns.Add("Balance", typeof(int));

            //Derek 131007

            for (int i = 0; i < GridView7.RowCount; i++)
            {
                int rowHandle = GridView7.GetVisibleRowHandle(i);
                DataRow row = GridView7.GetDataRow(rowHandle);

                if (GridView7.IsDataRow(rowHandle))
                {
                    if (GridView7.GetRowCellValue(rowHandle, "UtlCheck").ToString().ToUpper() == "TRUE")
                    {                        
                        DataRow newPackageRow = dtSelectedPackages.NewRow();
                        if (Convert.ToInt32(GridView7.GetRowCellValue(rowHandle, "nStatusID")) != 0)
                        {
                            MessageBox.Show("Invalid package(s) selected, please make sure the package(s) selected have not been converted, suspended, expired, refunded or balance is more than 0!");
                            return;
                        }
                        newPackageRow["UtlCheck"] = GridView7.GetRowCellValue(rowHandle, "UtlCheck").ToString();
                        newPackageRow["nPackageID"] = Convert.ToInt32(GridView7.GetRowCellValue(rowHandle, "nPackageID"));
                        newPackageRow["strMembershipID"] = GridView7.GetRowCellValue(rowHandle, "strMembershipID").ToString();
                        newPackageRow["strPackageCode"] = GridView7.GetRowCellValue(rowHandle, "strPackageCode").ToString();
                        newPackageRow["strDescription"] = GridView7.GetRowCellValue(rowHandle, "strDescription").ToString();
                        newPackageRow["nStatusID"] = Convert.ToInt32(GridView7.GetRowCellValue(rowHandle, "nStatusID"));
                        newPackageRow["nCategoryID"] = Convert.ToInt32(GridView7.GetRowCellValue(rowHandle, "nCategoryID"));
                        newPackageRow["Balance"] = Convert.ToInt32(GridView7.GetRowCellValue(rowHandle, "Balance"));

                        dtSelectedPackages.Rows.Add(newPackageRow);

                        curDivision = GetPackageDivisionByCategory(Convert.ToInt32(GridView7.GetRowCellValue(rowHandle, "nCategoryID")));

                        DataSet ds = new DataSet();

                        string strSQL = "select npackageid from tblMemberPackage mp left join tblReceipt r on mp.strReceiptNo=r.strReceiptNo left join tblPackage p on mp.strPackageCode=p.strPackageCode " +
                                        "where mp.strReceiptNo is not null and r.strReceiptNo is null and nStatusID=0 and mp.strRemarks not like '%PKG MISSING FROM SYSTEM%' " +
                                        "and mp.strReceiptNo not like '%FOC%' and fFree=0 and p.nCategoryID not in (23,14) and nPackageID=" + newPackageRow["nPackageID"].ToString();
                        SqlHelper.FillDataset(connection, CommandType.StoredProcedure, "UP_GETDATA", ds, new string[] { "Table" }, new SqlParameter("@strSQL", strSQL));

                        if (ds.Tables[0].Rows.Count > 0)
                        {
                            MessageBox.Show("Not allow to use this package as not receipt found.", "Warning", MessageBoxButtons.OK);
                            return;
                        }

                        if (myTMP.GetMemberPackageUtilizationPermission(Convert.ToInt32(GridView7.GetRowCellValue(rowHandle, "nStatusID")), Convert.ToInt32(GridView7.GetRowCellValue(rowHandle, "Balance"))) == 0)
                        {
                            MessageBox.Show("Invalid package(s) selected, please make sure the package(s) selected have not been converted, suspended, refunded or balance is more than 0!");
                            return;
                        }
                        if (Convert.ToInt32(row["nCategoryID"]) == 3 && Convert.ToInt32(row["nStatusID"]) != 0)
                        {
                            MessageBox.Show("Invalid package(s) selected, please make sure the PT package(s) selected have not been expired, converted, suspended, refunded or balance is more than 0!");
                            return;
                        }
                        if (!(curDivision == 0 || prevDivision == 0) && !(curDivision == prevDivision))
                        {
                            IsMixedCatSelected = true;
                            MessageBox.Show("Mixed categories of Spa, PT or Fitness are selected, new multiple service session can only be initiated for selected packages of the same category!");
                            return;
                        }
                        if (PackageHasOutStanding(Convert.ToInt32(newPackageRow["nPackageID"])))
                        {
                            double usedAmount;
                            double paidAmount;

                            TblMemberPackage sqlMemberPackage = new TblMemberPackage();
                            TblMember sqlMember = new TblMember();

                            sqlMember.StrMembershipID = newPackageRow["strMembershipID"].ToString();

                            DataTable memberPackageBalanceTable = sqlMemberPackage.GetMemberPackage(Convert.ToInt32(newPackageRow["nPackageID"]));
                            DataTable memberTable = sqlMember.SelectOne();

                            myMemberPackage.CalculateMemberPackageBalance(newPackageRow["strMembershipID"].ToString(), memberPackageBalanceTable);

                            usedAmount = ACMS.Convert.ToDouble(memberPackageBalanceTable.Rows[0]["usedAmt"]);
                            paidAmount = ACMS.Convert.ToDouble(memberPackageBalanceTable.Rows[0]["paidAmt"]);

                            if (usedAmount > paidAmount * 0.7)
                            {
                                MessageBox.Show(memberTable.Rows[0]["strMemberName"].ToString() + " had used sessions value is more than the paid amount.\n\nPlease pay outstading before proceed!", "Warning", MessageBoxButtons.OK);
           
                                return;
                            }
                        }
                        IsUtlBoxChecked = true;

                        prevDivision = curDivision;
                    }          
                }
            }

            //if (IsUtlBoxChecked)
            //{
                //OnNewClassAttendance_ServiceSession(dtSelectedPackages, curDivision);
            //}
            //else
            //{
                //OnNewClassAttendance_ServiceSession();
            //} 

            if(!IsUtlBoxChecked)
            {
                DataRow row = GridView7.GetDataRow(GridView7.FocusedRowHandle);


           

                DataRow newRow = dtSelectedPackages.NewRow();

                if (row != null)
                {
                    DataRow newPackageRow = dtSelectedPackages.NewRow();

                    if (Convert.ToInt32(row["nStatusID"]) != 0)
                    {
                        MessageBox.Show("Invalid package(s) selected, please make sure the package(s) selected have not been converted, suspended, expired, refunded or balance is more than 0!");
                        return;
                    }
                    newPackageRow["UtlCheck"] = row["UtlCheck"].ToString();
                    newPackageRow["nPackageID"] = Convert.ToInt32(row["nPackageID"]);
                    newPackageRow["strMembershipID"] = row["strMembershipID"].ToString();
                    newPackageRow["strPackageCode"] = row["strPackageCode"].ToString();
                    newPackageRow["strDescription"] = row["strDescription"].ToString();
                    newPackageRow["nStatusID"] = Convert.ToInt32(row["nStatusID"]);
                    newPackageRow["nCategoryID"] = Convert.ToInt32(row["nCategoryID"]);
                    newPackageRow["Balance"] = Convert.ToInt32(row["Balance"]);
                    
                    string npPackageID = (Convert.ToInt32(row["nPackageID"])).ToString();
                    //string dope = (GridView7.SelectRow(1)).ToString();
                    //GridView7[1,1].Selected = true;

               



                      //  == npPackageID;

                    dtSelectedPackages.Rows.Add(newPackageRow);

                    DataRow testx = GridView7.GetFocusedDataRow();

                    testx.Delete();
                    testx

                    //foreach (DataRow test in dtSelectedPackages.Rows)
                    //{
                    //    if (test["nPackageID"].ToString() == npPackageID)
                    //    {
                    //        DataRow[] test1 = dtSelectedPackages.Select("nPackageID = '" + npPackageID+ "'");
                    //        DataRow newRow123 = dtSelectedPackages.NewRow();

                    //        newRow123.ItemArray = test1[0].ItemArray;
                    //        dtSelectedPackages.Rows.Remove(test1[0]);
                    //        dtSelectedPackages.Rows.InsertAt(newRow123,0);

                    //        break;
                    //    }

                    //}


                    //string selectedTest = (from DataRow dr in dtSelectedPackages.Rows where (string)dr["nPackageID"] == npPackageID select (string)dr["npPackageID"]).FirstOrDefault();


                    curDivision = GetPackageDivisionByCategory(Convert.ToInt32(row["nCategoryID"]));

                    /*if (Convert.ToInt32(row["nStatusID"]) != 0 ||
                        Convert.ToInt32(row["Balance"]) <= 0)*/
                    if (myTMP.GetMemberPackageUtilizationPermission(Convert.ToInt32(row["nStatusID"]), Convert.ToInt32(row["Balance"])) == 0)
                    {
                        MessageBox.Show("Invalid package(s) selected, please make sure the package(s) selected have not been converted, suspended, refunded or balance is more than 0!");
                        return;
                    }
                    if (Convert.ToInt32(row["nCategoryID"]) == 3 && Convert.ToInt32(row["nStatusID"])!=0)
                    {
                        MessageBox.Show("Invalid package(s) selected, please make sure the PT package(s) selected have not been expired, converted, suspended, refunded or balance is more than 0!");
                        return;
                    }
                    if (PackageHasOutStanding(Convert.ToInt32(newPackageRow["nPackageID"])))
                    {
                        double usedAmount;
                        double paidAmount;

                        TblMemberPackage sqlMemberPackage = new TblMemberPackage();
                        TblMember sqlMember = new TblMember();

                        sqlMember.StrMembershipID = newPackageRow["strMembershipID"].ToString();

                        DataTable memberPackageBalanceTable = sqlMemberPackage.GetMemberPackage(Convert.ToInt32(newPackageRow["nPackageID"]));
                        DataTable memberTable = sqlMember.SelectOne();

                        myMemberPackage.CalculateMemberPackageBalance(newPackageRow["strMembershipID"].ToString(), memberPackageBalanceTable);

                        usedAmount = ACMS.Convert.ToDouble(memberPackageBalanceTable.Rows[0]["usedAmt"]);
                        paidAmount = ACMS.Convert.ToDouble(memberPackageBalanceTable.Rows[0]["paidAmt"]);

                        if (usedAmount > paidAmount * 0.7)
                        {
                            MessageBox.Show(memberTable.Rows[0]["strMemberName"].ToString() + " had used sessions value is more than the paid amount.\n\nPlease pay outstading before proceed!", "Warning", MessageBoxButtons.OK);

                            return;
                        }
                    }
                }
            }

            if (dtSelectedPackages.Rows.Count > 0)
            {
                if (curDivision == 1)
                {
                    if (!employee.HasRight("AB_INSERT_ATTENDANCE"))
                    {
                        MessageBox.Show(this, "You do not have permission to perform this action!");
                        return;
