                else if (curDivision == 2 || curDivision == 3) 
                {
                    if (!employee.HasRight("AB_INSERT_SERVICE_SESSION"))
                    {
                        MessageBox.Show(this, "You do not have permission to perform this action!");
                        return;
                    }
                }

                DataView dv = dtSelectedPackages.DefaultView;
                dv.Sort = "strDescription ASC, Balance ASC";
                DataTable sortedDT = dv.ToTable();

                OnNewClassAttendance_ServiceSession(sortedDT, curDivision);
            }
