        private int GetPackageDivisionByCategory(int nCategoryID)
        {
            int nDivision = 0;

            if (nCategoryID == 1 || nCategoryID == 2)
            {
                nDivision = 1;
            } 
            else if (nCategoryID == 4 || nCategoryID == 5 || nCategoryID == 6 || nCategoryID == 34)
            {
                nDivision = 2;
            }
            else if (nCategoryID == 3)
            {
                nDivision = 3;
            }
            return nDivision;
        }

