        private void sbtnOpenToMobileAppUser_Click(object sender, System.EventArgs e)
        {            
            TblWaitingList wl = new TblWaitingList();
            DataTable dt = wl.GetWaitingListBaseClassInstanceIDWithValidPackage(NClassInstanceID);

            DataSet _ds1 = new DataSet();
            DataTable _dt1 = new DataTable();
            SqlHelper.FillDataset(connection, CommandType.StoredProcedure, "sp_GetClassMaxNo", _ds1, new string[] { "Table" }, new SqlParameter("@nClassInstanceID", NClassInstanceID));

            int nAvailableSlot = Convert.ToInt16(_ds1.Tables[0].Rows[0][0]) - (GridView19.RowCount + GridView20.RowCount);
            if (dt.Rows.Count > 0 && nAvailableSlot > 0)
            {
                string strAvailableSlot = (nAvailableSlot == 1 ? "is 1 slot" : "are " + nAvailableSlot.ToString() + " slots");
                DialogResult result = MessageBox.Show("The following message will be blasted to mobile app waiting list members. \n\n " +
                    dt.Rows[0]["strDescription"].ToString() + " by " + dt.Rows[0]["strInstructorName"].ToString() + " is now available at " + dt.Rows[0]["strBranchName"].ToString() + " at " + Convert.ToDateTime(dt.Rows[0]["dtStartTime"]).ToString("h.mmtt") + " on " + Convert.ToDateTime(dt.Rows[0]["dtStartTime"]).ToString("dd MMM") + ". There " + strAvailableSlot + " available now. Please note that booking is based on first-come-first-served basis. Would you like to confirm booking? \n\nAre you sure?", "Warning", MessageBoxButtons.YesNo);

                if (result == DialogResult.Yes)
                {
                    foreach (DataRow r in dt.Rows)
                    {
                        if (r["strType"].ToString() == "Mobile App")
                        {
                            string strMsgContent = r["strDescription"].ToString() + " by " + r["strInstructorName"].ToString() + " is now available at " + r["strBranchName"].ToString() + " at " + Convert.ToDateTime(r["dtStartTime"]).ToString("h.mmtt") + " on " + Convert.ToDateTime(r["dtStartTime"]).ToString("dd MMM") + ". There " + strAvailableSlot + " available now. Please note that booking is based on first-come-first-served basis. Would you like to confirm booking%3F";                            
                            PushNotificationForWaitingListReply(r["strMembershipID"].ToString(), strMsgContent, Convert.ToInt32(r["nWaitingListID"]));
                        }
                    }
                }
            }
            if (nAvailableSlot <= 0)
                MessageBox.Show("No available slots.");
        }
