        private int CalculateUsage(int packageID)
        {
            ACMSDAL.TblMemberPackage memberPackage = new ACMSDAL.TblMemberPackage();
            memberPackage.NPackageID = packageID;
            DataTable tblmemberpackage = memberPackage.SelectOne();
            ACMSDAL.TblServiceSession MainUsage = new TblServiceSession();
            MainUsage.NPackageID = packageID;
            DataView serviceSessionTable = MainUsage.SelectAllWnPackageIDLogic().DefaultView;
            serviceSessionTable.RowFilter = "nStatusID = 5 or nStatusID = 6";
            int nMainUsage = serviceSessionTable.Count;

            // jackie 4/3/2012 why must compare the freebiz
            DataTable tblFreePackage = memberPackage.SelectFreePackage();
            if (tblFreePackage.Rows.Count > 0)
            {
                ACMSDAL.TblServiceSession FreebieUsage = new TblServiceSession();
                FreebieUsage.NPackageID = ACMS.Convert.ToSqlInt32(tblFreePackage.Rows[0]["nPackageID"]);
                DataView freeSessionTable = FreebieUsage.SelectAllWnPackageIDLogic().DefaultView;
                freeSessionTable.RowFilter = "nStatusID = 5 or nStatusID = 6";
                int nFreebieUsage = freeSessionTable.Count;

                if (nMainUsage == nFreebieUsage)
                    return nMainUsage;
                else
                    return -1;
            }
            else
            {
                return -1;
            }
        }

        private int CalculateSInglePackageUsage(int packageID)
        {
            ACMSDAL.TblMemberPackage memberPackage = new ACMSDAL.TblMemberPackage();
            memberPackage.NPackageID = packageID;
            DataTable tblmemberpackage = memberPackage.SelectOne();
            ACMSDAL.TblServiceSession MainUsage = new TblServiceSession();
            MainUsage.NPackageID = packageID;
            DataView serviceSessionTable = MainUsage.SelectAllWnPackageIDLogic().DefaultView;
            serviceSessionTable.RowFilter = "nStatusID = 5 or nStatusID = 6";
            int nMainUsage = serviceSessionTable.Count;
            return nMainUsage;
        }

        private void ConvertMemberPackage(int packageID, decimal mConvert, decimal mPaid)
        {
            // insert converted amount and 
            TblMemberPackage myMemberPackage = new TblMemberPackage();
            myMemberPackage.InsertMemberPackageConvertion(packageID, mConvert);
            //update package status               
            myMemberPackage.NPackageID = packageID;
            myMemberPackage.NStatusID = 3;
            myMemberPackage.MTotalPaid = mPaid;
            myMemberPackage.UpdatePackageStatus();
            //update free package status
            myMemberPackage.NPackageID = packageID;      
            DataTable tblFreePackage = myMemberPackage.SelectFreePackage();
            if (tblFreePackage.Rows.Count > 0)
            {
                int nFreePackage = ACMS.Convert.ToInt32(tblFreePackage.Rows[0]["nPackageID"]);
                TblMemberPackage myFreePackage = new TblMemberPackage();
                myFreePackage.NPackageID = nFreePackage;
                myFreePackage.NStatusID = 3;
                myFreePackage.UpdatePackageStatus();
            }
        }

