        private void simpleButton90_Click(object sender, EventArgs e)
        {
            ACMS.XtraUtils.GridViewUtils.UpdateData(GridView7);
            DataRow row = GridView7.GetDataRow(GridView7.FocusedRowHandle);
            if (row != null)
            {
                if (ACMS.Convert.ToDBInt32(row["nStatusID"]) == 3)
                {
                    MessageBox.Show("You cant Upgrade Converted Package");
                    return;
                }

				ACMSDAL.TblMemberPackage sqlCalcAnyOS1 = new ACMSDAL.TblMemberPackage();
                decimal dOutAmount = sqlCalcAnyOS1.OutstandingAmount(row["strMembershipID"].ToString());
                DataRow rCurrent = gvMemberRecord.GetDataRow(gvMemberRecord.FocusedRowHandle);
                if (dOutAmount > 0)
                {
                    DialogResult result1 = MessageBox.Show(this, rCurrent["strMemberName"].ToString() + " has Outstanding amount of " + (string.Format("{0:C}", dOutAmount)) + "\nDo you want to continue?", "Warning",
                        MessageBoxButtons.YesNo);

                    if (result1 == DialogResult.No)
                        return;
                }
                if (ACMS.Convert.ToDBBoolean(row["fFree"]) == false)
                {
                    int nPackageID = ACMS.Convert.ToDBInt32(row["nPackageID"]);
                    string memberID = row["strMembershipID"].ToString();
                    string strFreePackageCode = row["strFreePkgCode"].ToString();

                    //Check the upgrade package got freee package??
                    if (strFreePackageCode == "")
                    {
                        int nWithoutFreePackage = CalculateSInglePackageUsage(nPackageID);
                        FormConvertMemberPackage form = new FormConvertMemberPackage(memberID, nPackageID, nWithoutFreePackage);
                        DialogResult result = form.ShowDialog();

                        if (result == DialogResult.OK)
                        {
                            // insert converted amount and update package status
                            ConvertMemberPackage(nPackageID, form.mConvertAmount,form.mPayAmount);
                            InitMemberPackage(CurrentMembershipID);
                        }
                    }
                    else
                    {
                        // check if free usage = main usage
                        int fPackageSession = CalculateUsage(nPackageID);
                        if (fPackageSession >= 0)
                        {
                            FormConvertMemberPackage form = new FormConvertMemberPackage(memberID, nPackageID, fPackageSession);
                            DialogResult result = form.ShowDialog();

                            if (result == DialogResult.OK)
                            {
                                // insert converted amount and update package status
                                ConvertMemberPackage(nPackageID, form.mConvertAmount,form.mPayAmount);
                                InitMemberPackage(CurrentMembershipID);
                            }
                        }
                        else
                        {
                            MessageBox.Show("Main package usage must be same as Free package usage");
                        }
                    }
                }
                else
                {
                    MessageBox.Show("You can't upgrade a free package");
                }
            }
        }

