		private void gridViewShift_LostFocus(object sender, EventArgs e)
        {
            DataRow row = this.gridViewShift.GetDataRow(gridViewShift.FocusedRowHandle);

            if (row != null)
            {
                if (row["dtBankInDate"] != DBNull.Value && row["nBankInStaffID"] != DBNull.Value)
                {
                    string strSQL;
                    DataSet _ds;
                    gridViewShift.CloseEditor();
                    gridViewShift.UpdateCurrentRow();

                    strSQL = "update tblShift SET dtBankInDate='" + Convert.ToDateTime(row["dtBankInDate"]).ToString("yyyy-MM-dd") + "',nBankInStaffID=" + row["nBankInStaffID"].ToString() + " where strBranchCode='" + row["strBranchCode"].ToString() + "' and nTerminalID=" + row["nTerminalID"] + " and nShiftID=" + row["nShiftID"] + " and Convert(varchar(10),dtDate,101)='" + Convert.ToDateTime(row["dtDate"]).ToString("MM/dd/yyyy") + "' ";
                    _ds = new DataSet();
                    SqlHelper.FillDataset(connection, CommandType.StoredProcedure, "UP_GETDATA", _ds, new string[] { "table" }, new SqlParameter("@strSQL", strSQL));
                    _ds.Dispose();
                }                
            }            
        }

