        private void OnDeleteMemberCreditPackageExtension()
        {
            employee.RightsLevel.Id = 9;

            if (!employee.HasRight("AB_DELETE_MEMBER_PACKAGE_EXTENSION"))
            {
                MessageBox.Show(this, "You are not allow to perform this action.");
                return;
            }           

            DataRow r = gridView38.GetDataRow(gridView38.FocusedRowHandle);

            if (r != null)
            {
                int nExtensionID = ACMS.Convert.ToInt32(r["nExtensionID"]);
                int nCreditPackageID = ACMS.Convert.ToInt32(r["nCreditPackageID"]);

                DialogResult result = MessageBox.Show(this, "Do you really want to delete this credit package extension?", "Delete?", MessageBoxButtons.YesNo);

                if (result == DialogResult.Yes)
                {
                    try
                    {
                        myCreditAccount.DeleteLastCreditPackageExtensionHistory(nCreditPackageID, nExtensionID, CurrentMembershipID);

                        RefreshMemberCreditPackageModule();
                    }
                    catch (Exception ex)
                    {
                        MessageBox.Show(ex.Message);
                    }
                }
            }
        }

