        private void OnNewCreditPackageExtension()
        {
            DataRow row = GridView11.GetDataRow(GridView11.FocusedRowHandle);

            //Check Selected Row ISNULL
            if (row != null)
            {
                int nCreditPackageID = Convert.ToInt32(row["nCreditPackageID"]);
                string dtPurchaseDate = row["dtPurchaseDate"].ToString();
                string dtStartDate = row["dtStartDate"].ToString();
                string dtExpiryDate = row["dtExpiryDate"].ToString();
                int nStatusID = Convert.ToInt32(row["nStatusID"]);

                //Check if user has the rights to extend package
                employee.RightsLevel.Id = 8;

                if (!employee.HasRight("AB_EXTEND_PACKAGE_USING_OTHERS_AS_TYPE"))
                {
                    MessageBox.Show(this, "You are not authorised to perform credit package extension.");
                    return;
                }

                //Converted Credit packages cannot be extend
                if (ACMS.Convert.ToDBInt32(row["nStatusID"]) == 3)
                {
                    MessageBox.Show("You Can't Extend Converted Package");
                    return;
                }

                if (ACMS.Convert.ToDBInt32(row["nStatusID"]) == 4)
                {
                    MessageBox.Show("You Can't Extend Refunded Package");
                    return;
                }

                //Check if member credit package has been used already or not
                if (row["dtStartDate"].ToString() == "")
                {
                    MessageBox.Show(this, "Credit Package not in use yet");
                    return;
                }
                else if (row["dtExpiryDate"].ToString() == "")
                {
                    MessageBox.Show(this, "Credit Package not in use yet");
                    return;
